pragma solidity ^0.8.20;

import "./OdosSwapUtils.sol";
import "./interface/IOdosRouterV2.sol";
import "@rari-capital/solmate/src/utils/SafeTransferLib.sol";

/**
 * @title OdosSwapper
 * @notice Contract for executing Odos swaps using OdosSwapUtils library
 */
contract OdosSwapper {
    using OdosSwapUtils for *;
    using SafeTransferLib for ERC20;

    IOdosRouterV2 public immutable router;

    constructor(address _router) {
        router = IOdosRouterV2(payable(_router));
    }

    /**
     * @notice Performs an swap operation using Odos router with swap data
     * @param inputToken Input token
     * @param maxIn Maximum input amount
     * @param exactOut Exact output amount
     * @param swapData Encoded swap path data
     */
    function executeSwapOperation(
        address inputToken,
        uint256 maxIn,
        uint256 exactOut,
        bytes calldata swapData
    ) external {
        ERC20(inputToken).safeTransferFrom(msg.sender, address(this), maxIn);
        OdosSwapUtils.excuteSwapOperation(
            router,
            inputToken,
            maxIn,
            exactOut,
            swapData
        );
    }
}

contracts/odos/OdosSwapUtils.sol.content:
// SPDX-License-Identifier: GNU AGPLv3
pragma solidity ^0.8.20;

import "./interface/IOdosRouterV2.sol";
import "@rari-capital/solmate/src/utils/SafeTransferLib.sol";

/**
 * @title OdosSwapUtils
 * @notice Library for handling Odos swaps in liquidator contracts
 */
library OdosSwapUtils {
    using SafeTransferLib for ERC20;

    /// @notice Custom error for failed swap with no revert reason
    error SwapFailed();
    /// @notice Custom error when actual output amount is less than expected
    error InsufficientOutput(uint256 expected, uint256 actual);

    /**
     * @notice Performs an swap operation using Odos router with swap data
     * @param router Odos router contract
     * @param inputToken Input token
     * @param maxIn Maximum input amount
     * @param exactOut Exact output amount
     * @param swapData Encoded swap path data
     */
    function excuteSwapOperation(
        IOdosRouterV2 router,
        address inputToken,
        uint256 maxIn,
        uint256 exactOut,
        bytes memory swapData
    ) internal returns (uint256) {
        // Add a safety buffer (5% extra) on maxIn to make sure we have enough approval
        // This is a workaround for the fact that the approval amount and swap amount are calculated differently
        uint256 approvalAmount = maxIn + (maxIn * 5) / 100;
        ERC20(inputToken).approve(address(router), approvalAmount);

        (bool success, bytes memory result) = address(router).call(swapData);

        // Always reset approval to 0 for security, regardless of success/failure
        ERC20(inputToken).approve(address(router), 0);

        if (!success) {
            // Decode the revert reason if present
            if (result.length > 0) {
                // First try to decode the standard revert reason
                assembly {
                    let resultLength := mload(result)
                    revert(add(32, result), resultLength)
                }
            }
            revert SwapFailed();
        }

        uint256 actualAmountOut;
        assembly {
            actualAmountOut := mload(add(result, 32))
        }

        if (actualAmountOut < exactOut) {
            revert InsufficientOutput(exactOut, actualAmountOut);
        }

        return actualAmountOut;
    }
}

